sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	var body = "{\"fields\":{\"project\":{\"key\":\"SI\"},\"summary\": \"This is JIRA demo summary.\",\"description\":\"Demo JIRA description\",\"issuetype\": {\"name\":\"Story\"}}}";
	var request = new sn_ws.RESTMessageV2();
	request.setEndpoint('https://arunsharmaasr3007.atlassian.net/rest/api/2/issue');
	request.setHttpMethod('POST');
	request.setRequestBody(body);

	var user = gs.getProperty('x_682682_jira_inte.jiraUser');
	var password = gs.getProperty('x_682682_jira_inte.jiraPwd');

	request.setBasicAuth(user, password);
	request.setRequestHeader("Accept", "application/json");
	request.setRequestHeader("Content-Type", "application/json");
	gs.log("Request sent to JIRA");
	var response = request.execute();
	gs.log("Response from Jira Issue"+ response.getStatusCode());
	gs.log(response.getBody());

})(current, previous);
